using UnityEngine; using System.Collections; using UnityEngine.Audio;  public class MarketPlay : MonoBehaviour {     public AudioMixerSnapshot inMarket;     public AudioMixerSnapshot outOfMarket;      private float m_TransitionIn;     private float m_TransitionOut;          // Use this for initialization     void Start () {         m_TransitionIn = 1.5f;         m_TransitionOut = 2.5f;     }      void OnTriggerEnter(Collider market)     {         if (market.CompareTag("VillageZone") && market)         {             inMarket.TransitionTo(m_TransitionIn);         }     }      void OnTriggerExit(Collider market)     {         if (market.CompareTag("VillageZone") && market)         {             outOfMarket.TransitionTo(m_TransitionOut);         }     }     // Update is called once per frame     void Update () {      } } 